import { Component } from '@angular/core';
import { NgModule } from '@angular/core';
import { NgbModule } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-our-team',
  standalone: true,
  templateUrl: './our-team.component.html',
  styleUrls: ['./our-team.component.css'],
  imports: [
    NgbModule
  ]
})
export class OurTeamComponent {
  teamMembers = [
    {
      name: 'John Doe',
      photo: 'assets/team/john-doe.jpg',
      role: 'CEO'
    },
    {
      name: 'Jane Smith',
      photo: 'assets/team/jane-smith.jpg',
      role: 'CTO'
    },
    {
      name: 'Bob Johnson',
      photo: 'assets/team/bob-johnson.jpg',
      role: 'Developer'
    }
  ];
}
<div class="row">
    <div class="col-sm-4" *ngFor="let teamMember of teamMembers">
        <ngb-card>
            <img class="card-img-top" src="{{ teamMember.photo }}" alt="{{ teamMember.name }}">
            <div class="card-body">
              <h5 class="card-title">{{ teamMember.name }}</h5>
              <p class="card-text">{{ teamMember.role }}</p>
            </div>
          </ngb-card>


    </div>
  </div>
