import { Component } from '@angular/core';
import { NgbCarouselConfig } from '@ng-bootstrap/ng-bootstrap';

@Component({
  selector: 'app-carousel-comp',
  templateUrl: './carousel-comp.component.html',
  styleUrls: ['./carousel-comp.component.css'],
  providers: [NgbCarouselConfig]
})
export class CarouselCompComponent {
  showNavigationArrows = false;
  showNavigationIndicators = false;
  images = [
    { title: 'First Slide', short: 'First Slide Short', src: "../../assets/images/cd.jpg" },
    { title: 'Second Slide', short: 'Second Slide Short', src: "../../assets/images/cd.jpg" },
    { title: 'Third Slide', short: 'Third Slide Short', src: "../../assets/images/cd.jpg" }
  ];

  constructor(config: NgbCarouselConfig) {
    // customize default values of carousels used by this component tree
    config.showNavigationArrows = true;
    config.showNavigationIndicators = true;
    config.interval = 2000;
    config.keyboard = true;
    config.pauseOnHover = true;
  }
}
