import { Component } from '@angular/core';
import { NgbCarouselConfig, NgbCarouselModule } from '@ng-bootstrap/ng-bootstrap';
import { NgFor, NgIf } from '@angular/common';

@Component({
  selector: 'app-carousel-comp',
  standalone: true,
	imports: [NgbCarouselModule, NgIf, NgFor],
  templateUrl: './carousel-comp.component.html',
  styleUrls: ['./carousel-comp.component.css'],
  providers: [NgbCarouselConfig], // add NgbCarouselConfig to the component providers
})
export class CarouselCompComponent {
  showNavigationArrows = false;
	showNavigationIndicators = false;
	images = [100, 194, 368].map((n) => `https://picsum.photos/id/${n}/900/500`);

	constructor(config: NgbCarouselConfig) {
		// customize default values of carousels used by this component tree
		config.showNavigationArrows = true;
		config.showNavigationIndicators = true;
	}
}
<ngb-carousel
	*ngIf="images"
	[showNavigationArrows]="showNavigationArrows"
	[showNavigationIndicators]="showNavigationIndicators"
>
	<ng-template ngbSlide *ngFor="let image of images">
		<div class="picsum-img-wrapper">
			<img [src]="" alt="Random slide" />
		</div>
		<div class="carousel-caption">
			<h3>No mouse navigation</h3>
			<p>This carousel hides navigation arrows and indicators.</p>
		</div>
	</ng-template>
</ngb-carousel>

<hr />

<div class="btn-group" role="group" aria-label="Carousel toggle controls">
	<button type="button" (click)="showNavigationArrows = !showNavigationArrows" class="btn btn-outline-dark btn-sm">
		Toggle navigation arrows
	</button>
	<button
		type="button"
		(click)="showNavigationIndicators = !showNavigationIndicators"
		class="btn btn-outline-dark btn-sm"
	>
		Toggle navigation indicators
	</button>
</div>
